<?phprequire_once('nusoap.php');$server = new soap_server();$server->configureWSDL('hellowsdl','urn:hellowsdl');$server->wsdl->schemaTargetNamespace = 'urn:hellowsdl';$server->register('hello',  //nama metodearray('angka1' => 'xsd:string', 'angka2' => 'xsd:string'),    //parameter inputarray('return' => 'xsd:string'),    //parametern output'urn:hellowsdl',    //namespace'urn:hellowsdl#hello',    //soapaction'rpc',        //style'encoded',    //use'Says hello to the caller'    //dokumentasi);$HTTP_RAW_POST_DATA = isset($HTTP_RAW_POST_DATA)? $HTTP_RAW_POST_DATA : '';$server->service($HTTP_RAW_POST_DATA);//functionfunction hello($angka1, $angka2){	return $angka1+$angka2;}?>